function PlayerTeamname(team) {
  let res;
  switch(team) {
    case "ATL":
      res = 'Atlanta Hawks'
      break
    case "BKN":
      res = 'Brooklyn Nets'
      break
    case "BOS":
      res = 'Boston Celtics'
      break
    case "CHA":
      res = 'Charlotte Hornets'
      break
    case "CHI":
      res = 'Chicago Bulls'
      break
    case "CLE":
      res = 'Cleveland Cavaliers'
      break
    case "DAL":
      res = 'Dallas Mavericks'
      break
    case "DEN":
      res = 'Denver Nuggets'
      break
    case "DET":
      res = 'Detroit Pistons'
      break
    case "GSW":
      res = 'Golden State Warriors'
      break
    case "HOU":
      res = 'Houston Rockets'
      break
    case "IND":
      res = 'Indiana Pacers'
      break
    case "LAC":
      res = 'Los Angeles Clippers'
      break
    case "LAL":
      res = 'Los Angeles Lakers'
      break
    case "MEM":
      res = 'Memphis Grizzlies'
      break
    case "MIA":
      res = 'Miami Heat'
      break
    case "MIL":
      res = 'Milwaukee Bucks'
      break
    case "MIN":
      res = "Minnesota Timberwolves"
      break
    case "NOP":
      res = 'New Orleans Pelicans'
      break
    case "NYK":
      res = 'New York Knicks'
      break
    case "OKC":
      res = 'Oklahoma City Thunder'
      break
    case "ORL":
      res = 'Orlando Magic'
      break
    case "PHI":
      res = 'Philidelphia 76ers'
      break
    case "PHX":
      res = "Phoenix Suns"
      break
    case "POR":
      res = "Portland Trailblazers"
      break
    case "SAC":
      res = "Sacramento Kings"
      break
    case "SAS":
      res = "San Antonio Spurs"
      break
    case "TOR":
      res = "Toronto Raptors"
      break
    case "UTA":
      res = "Utah Jazz"
      break
    case "WAS":
      res = "Washington Wizards"
      break
  }
  return res
}


export default PlayerTeamname
